            <article id="node-5208" class="node node-article node-teaser clearfix" about="/portal/content/%D8%B3%D9%88%D8%A7%D9%84%D8%A7%D8%AA-%D8%AA%DA%A9%D9%85%DB%8C%D9%84%DB%8C-%D9%85%D8%A8%D8%AD%D8%AB-%D8%AF%DB%8C%D9%86%D8%A7%D9%85%DB%8C%DA%A9-%D9%81%DB%8C%D8%B2%DB%8C%DA%A9-%D8%AF%D9%87%D9%85" typeof="sioc:Item foaf:Document">
